import React, { useState, useMemo, useCallback } from 'react';

const CounterComponent = () => {
    const [count, setCount] = useState(0);

    // Пример использования useMemo для мемоизации вычисляемого значения
    const squaredValue = useMemo(() => {
        console.log('Вычисление квадрата значения');
        return count * count;
    }, [count]);

    // Пример использования useCallback для мемоизации колбэк-функции
    const increment = useCallback(() => {
        console.log('Инкремент значения');
        setCount(prevCount => prevCount + 1);
    }, []);

    return (
        <div>
            <p>Текущее значение: {count}</p>
            <p>Квадрат значения: {squaredValue}</p>
            <button onClick={increment}>Увеличить значение</button>
        </div>
    );
};

export default CounterComponent;
