import React, { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';

const DataFetchingComponent = () => {
    const [data, setData] = useState([]);
    const [query, setQuery] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    // Пример использования useCallback для мемоизации колбэк-функции
    const fetchData = useCallback(async () => {
        try {
            setIsLoading(true);
            const response = await axios.get(`https://api.example.com/data?query=${query}`);
            setData(response.data);
        } catch (error) {
            console.error('Ошибка при загрузке данных:', error);
        } finally {
            setIsLoading(false);
        }
    }, [query]);

    // Пример использования useMemo для мемоизации результата API запроса
    const filteredData = useMemo(() => {
        console.log('Фильтрация данных');
        return data.filter(item => item.name.toLowerCase().includes(query.toLowerCase()));
    }, [data, query]);

    useEffect(() => {
        fetchData(); // Запускаем загрузку данных при монтировании компонента
    }, [fetchData]);

    return (
        <div>
            <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Введите запрос"
            />
            {isLoading ? (
                <p>Загрузка данных...</p>
            ) : (
                <ul>
                    {filteredData.map(item => (
                        <li key={item.id}>{item.name}</li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default DataFetchingComponent;
